//! Rust library for the Oxide “benchmark” Flutter example.
//!
//! The public API is consumed by Flutter via generated bindings.

/// FFI-facing API surface for the Flutter example.
pub mod api;
mod state;
mod reducers;
mod runtime;
mod frb_generated;

/// Counter workload engine generated by the `#[reducer(...)]` macro.
pub use api::counter_bridge::CounterEngine;
/// JSON workload engine generated by the `#[reducer(...)]` macro.
pub use api::json_bridge::JsonEngine;
/// Sieve workload engine generated by the `#[reducer(...)]` macro.
pub use api::sieve_bridge::SieveEngine;
/// Error type exposed across the FFI boundary.
pub use oxide_core::OxideError;
/// Result type used by the Rust-side API.
pub use oxide_core::{CoreResult, StateSnapshot};
/// Watch receiver type used for subscribing to state snapshots.
pub use tokio::sync::watch::Receiver;

#[cfg(test)]
mod tests;
